const ipad=window.matchMedia("screen and (max-width: 767px)"),menu=document.querySelector(".menu"),burgerButton=document.querySelector("#burger-menu");function validation(e){e.matches?burgerButton.addEventListener("click",hideShow):burgerButton.removeEventListener("click",hideShow)}function hideShow(){menu.classList.contains("is-active")?menu.classList.remove("is-active"):menu.classList.add("is-active")}ipad.addListener(validation),validation(ipad);